import { Component, OnInit } from '@angular/core';
import { TransactionService } from '../../services/transaction.service';
import { Transaction, TransactionType } from '../../models/transaction.model';

@Component({
  selector: 'app-transaction-list',
  templateUrl: './transaction-list.component.html',
  styleUrls: ['./transaction-list.component.scss']
})
export class TransactionListComponent implements OnInit {
  transactions: Transaction[] = [];
  filteredTransactions: Transaction[] = [];
  isLoading = true;
  error: string | null = null;

  // Filter options
  filterType: string = 'all';
  searchTerm: string = '';

  // Sort options
  sortField: 'date' | 'amount' | 'description' = 'date';
  sortDirection: 'asc' | 'desc' = 'desc';

  constructor(private transactionService: TransactionService) { }

  ngOnInit(): void {
    this.loadTransactions();
  }

  loadTransactions(): void {
    this.isLoading = true;
    this.transactionService.getAllTransactions().subscribe({
      next: (transactions) => {
        this.transactions = transactions;
        this.applyFilters();
        this.isLoading = false;
      },
      error: (err) => {
        this.error = 'Failed to load transactions';
        this.isLoading = false;
        console.error(err);
      }
    });
  }

  applyFilters(): void {
    let result = [...this.transactions];

    // Apply type filter
    if (this.filterType !== 'all') {
      result = result.filter(t => t.type === this.filterType);
    }

    // Apply search filter
    if (this.searchTerm.trim()) {
      const term = this.searchTerm.toLowerCase().trim();
      result = result.filter(t => 
        t.description.toLowerCase().includes(term) || 
        t.category.toLowerCase().includes(term)
      );
    }

    // Apply sorting
    result.sort((a, b) => {
      let comparison = 0;

      if (this.sortField === 'date') {
        comparison = new Date(a.date).getTime() - new Date(b.date).getTime();
      } else if (this.sortField === 'amount') {
        comparison = a.amount - b.amount;
      } else if (this.sortField === 'description') {
        comparison = a.description.localeCompare(b.description);
      }

      return this.sortDirection === 'asc' ? comparison : -comparison;
    });

    this.filteredTransactions = result;
  }

  onFilterChange(): void {
    this.applyFilters();
  }

  onSortChange(field: 'date' | 'amount' | 'description'): void {
    if (this.sortField === field) {
      // Toggle direction if clicking the same field
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortField = field;
      this.sortDirection = 'desc'; // Default to descending for new sort field
    }

    this.applyFilters();
  }

  getSortIndicator(field: string): string {
    if (this.sortField === field) {
      return this.sortDirection === 'asc' ? '↑' : '↓';
    }
    return '';
  }

  deleteTransaction(id: number | undefined): void {
    if (!id) {
      this.error = 'Cannot delete transaction: Invalid ID';
      return;
    }

    if (confirm('Are you sure you want to delete this transaction?')) {
      this.transactionService.deleteTransaction(id).subscribe({
        next: () => {
          this.transactions = this.transactions.filter(t => t.id !== id);
          this.applyFilters();
        },
        error: (err) => {
          this.error = 'Failed to delete transaction';
          console.error(err);
        }
      });
    }
  }
}
<div class="transaction-list-container">
  <div class="header">
    <h1>Transactions</h1>
    <a routerLink="/add-transaction" class="add-button">+ Add Transaction</a>
  </div>

  <div class="filters">
    <div class="search-box">
      <input 
        type="text" 
        [(ngModel)]="searchTerm" 
        (input)="onFilterChange()" 
        placeholder="Search transactions...">
    </div>

    <div class="filter-options">
      <select [(ngModel)]="filterType" (change)="onFilterChange()">
        <option value="all">All Transactions</option>
        <option value="INCOME">Income Only</option>
        <option value="EXPENSE">Expenses Only</option>
      </select>
    </div>
  </div>

  <div *ngIf="isLoading" class="loading">Loading transactions...</div>

  <div *ngIf="error" class="error-message">{{ error }}</div>

  <div *ngIf="!isLoading && !error && filteredTransactions.length === 0" class="empty-state">
    No transactions found. <a routerLink="/add-transaction">Add your first transaction</a>
  </div>

  <div *ngIf="filteredTransactions.length > 0" class="transaction-table-container">
    <table class="transaction-table">
      <thead>
        <tr>
          <th (click)="onSortChange('date')" class="sortable">
            Date {{ getSortIndicator('date') }}
          </th>
          <th (click)="onSortChange('description')" class="sortable">
            Description {{ getSortIndicator('description') }}
          </th>
          <th>Category</th>
          <th>Type</th>
          <th (click)="onSortChange('amount')" class="sortable text-right">
            Amount {{ getSortIndicator('amount') }}
          </th>
          <th class="actions-column">Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let transaction of filteredTransactions">
          <td>{{ transaction.date | date:'medium' }}</td>
          <td>{{ transaction.description }}</td>
          <td>{{ transaction.category }}</td>
          <td>
            <span class="transaction-type" [ngClass]="transaction.type.toLowerCase()">
              {{ transaction.type }}
            </span>
          </td>
          <td class="text-right" [ngClass]="{'income-amount': transaction.type === 'INCOME', 'expense-amount': transaction.type === 'EXPENSE'}">
            {{ transaction.amount | currency }}
          </td>
          <td class="actions-column">
            <div class="action-buttons">
              <a [routerLink]="['/edit-transaction', transaction.id]" class="edit-button">Edit</a>
              <button (click)="deleteTransaction(transaction.id)" class="delete-button">Delete</button>
            </div>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
