import { Component, OnInit } from '@angular/core';
import { TransactionService } from '../../services/transaction.service';
import { Transaction, TransactionType } from '../../models/transaction.model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  totalIncome: number = 0;
  totalExpenses: number = 0;
  balance: number = 0;

  dailyExpenses: number = 0;
  weeklyExpenses: number = 0;
  monthlyExpenses: number = 0;
  yearlyExpenses: number = 0;

  recentTransactions: Transaction[] = [];
  isLoading: boolean = true;
  error: string | null = null;

  constructor(private transactionService: TransactionService) { }

  ngOnInit(): void {
    this.loadFinancialSummary();
    this.loadRecentTransactions();
    this.loadPeriodExpenses();
  }

  loadFinancialSummary(): void {
    this.transactionService.getFinancialSummary().subscribe({
      next: (summary) => {
        this.totalIncome = summary.totalIncome;
        this.totalExpenses = summary.totalExpenses;
        this.balance = summary.balance;
      },
      error: (err) => {
        this.error = 'Failed to load financial summary';
        console.error(err);
      }
    });
  }

  loadRecentTransactions(): void {
    this.transactionService.getAllTransactions().subscribe({
      next: (transactions) => {
        this.recentTransactions = transactions
          .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime())
          .slice(0, 5);
        this.isLoading = false;
      },
      error: (err) => {
        this.error = 'Failed to load recent transactions';
        this.isLoading = false;
        console.error(err);
      }
    });
  }

  loadPeriodExpenses(): void {
    const today = new Date();

    // Calculate date ranges
    const dayStart = new Date(today);
    dayStart.setHours(0, 0, 0, 0);

    const weekStart = new Date(today);
    weekStart.setDate(today.getDate() - today.getDay());
    weekStart.setHours(0, 0, 0, 0);

    const monthStart = new Date(today.getFullYear(), today.getMonth(), 1);
    const yearStart = new Date(today.getFullYear(), 0, 1);

    // Format dates for API
    const formatDate = (date: Date) => date.toISOString();
    const nowFormatted = formatDate(today);

    // Get daily expenses
    this.transactionService.getTransactionsBetweenDates(
      formatDate(dayStart), 
      nowFormatted
    ).subscribe(transactions => {
      this.dailyExpenses = this.calculateTotalExpenses(transactions);
    });

    // Get weekly expenses
    this.transactionService.getTransactionsBetweenDates(
      formatDate(weekStart), 
      nowFormatted
    ).subscribe(transactions => {
      this.weeklyExpenses = this.calculateTotalExpenses(transactions);
    });

    // Get monthly expenses
    this.transactionService.getTransactionsBetweenDates(
      formatDate(monthStart), 
      nowFormatted
    ).subscribe(transactions => {
      this.monthlyExpenses = this.calculateTotalExpenses(transactions);
    });

    // Get yearly expenses
    this.transactionService.getTransactionsBetweenDates(
      formatDate(yearStart), 
      nowFormatted
    ).subscribe(transactions => {
      this.yearlyExpenses = this.calculateTotalExpenses(transactions);
    });
  }

  private calculateTotalExpenses(transactions: Transaction[]): number {
    return transactions
      .filter(t => t.type === TransactionType.EXPENSE)
      .reduce((sum, transaction) => sum + transaction.amount, 0);
  }
}
<div class="dashboard-container">
  <h1>Dashboard</h1>

  <div class="summary-section">
    <app-summary-card title="Balance" [amount]="balance" [isPositive]="balance >= 0"></app-summary-card>
    <app-summary-card title="Income" [amount]="totalIncome" [isPositive]="true"></app-summary-card>
    <app-summary-card title="Expenses" [amount]="totalExpenses" [isPositive]="false"></app-summary-card>
  </div>

  <div class="expenses-section">
    <h2>Expense Summary</h2>
    <div class="expense-cards">
      <div class="expense-card">
        <h3>Daily</h3>
        <p class="amount">{{ dailyExpenses | currency:'EUR' }}</p>
      </div>
      <div class="expense-card">
        <h3>Weekly</h3>
        <p class="amount">{{ weeklyExpenses | currency:'EUR' }}</p>
      </div>
      <div class="expense-card">
        <h3>Monthly</h3>
        <p class="amount">{{ monthlyExpenses | currency:'EUR' }}</p>
      </div>
      <div class="expense-card">
        <h3>Yearly</h3>
        <p class="amount">{{ yearlyExpenses | currency:'EUR' }}</p>
      </div>
    </div>
  </div>

  <div class="recent-transactions">
    <div class="header-row">
      <h2>Recent Transactions</h2>
      <a routerLink="/transactions" class="view-all">View All</a>
    </div>

    <div *ngIf="isLoading" class="loading">Loading transactions...</div>

    <div *ngIf="error" class="error-message">{{ error }}</div>

    <div *ngIf="!isLoading && !error && recentTransactions.length === 0" class="empty-state">
      No transactions found. <a routerLink="/add-transaction">Add your first transaction</a>
    </div>

    <div *ngIf="recentTransactions.length > 0" class="transaction-list">
      <div *ngFor="let transaction of recentTransactions" class="transaction-item">
        <div class="transaction-info">
          <div class="transaction-title">{{ transaction.description }}</div>
          <div class="transaction-category">{{ transaction.category }}</div>
          <div class="transaction-date">{{ transaction.date | date:'medium' }}</div>
        </div>
        <div class="transaction-amount" [ngClass]="{'income': transaction.type === 'INCOME', 'expense': transaction.type === 'EXPENSE'}">
          {{ transaction.type === 'INCOME' ? '+' : '-' }} {{ transaction.amount | currency:'EUR' }}
        </div>
      </div>
    </div>

    <div class="add-transaction-button">
      <a routerLink="/add-transaction" class="add-button">+ Add Transaction</a>
    </div>
  </div>
</div>
